package scalus.cardano.plutus.contract.blueprint
import scalus.Compile
import scalus.Compiler.compile
import scalus.builtin.Data
import scalus.prelude.Validator
import scalus.ledger.api.v3.{TxInfo, TxOutRef}
import scalus.prelude.Option
import scalus.{plutusV3, toUplcOptimized}

@Compile
object FakeValidator extends Validator:
    override def spend(
        datum: Option[Data],
        redeemer: Data,
        tx: TxInfo,
        ownRef: TxOutRef
    ): Unit = ()

object ValidBlueprints {
    object ForFakeValidator {
        val compiledFakeValidatorFn: Array[Byte] =
            compile(FakeValidator.validate).toUplcOptimized().plutusV3.cborEncoded
        def compiledAndHashed(title: String, description: String) =
            s"""{"preamble":{"title":"$title","description":"$description","compiler":{"name":"scalus","version":"0.10.0"},"plutusVersion":"v3"},"validators":[{"title":"validator","compiledCode":"","hash":"a99f455f3cdf6d00e1a81ec72951b3d81a6e4e13d4ff8810a1c6f73f"}]}"""
    }

}
